import React, { useContext } from "react";
// import Input from "../../shared/components/FormElements/Input";

import {
  VALIDATOR_REQUIRE,
  VALIDATOR_MINLENGTH,
  VALIDATOR_URL,
} from "../../shared/util/validators";
import Button from "../../shared/components/UIElements/Button";
import { useForm } from "../../shared/hooks/form-hook";
import { AuthContext } from "../../shared/context/auth-context";
import {
  FormControl,
  InputLabel,
  FormHelperText,
  Input,
  Grid,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      margin: theme.spacing(),
    },
    "& .MuiFormControl-root": {
      width: "80%",
      margin: theme.spacing(1),
    },
  },
}));

const NewWound = () => {
  const auth = useContext(AuthContext);
  const classes = useStyles();

  // initial values

  const [formState, changeHandler] = useForm(
    {
      name: {
        value: "",
        isValid: false,
      },
      bodyPart: {
        value: "",
        isValid: false,
      },
      description: {
        value: "",
        isValid: false,
      },
      woundImageUpload: {
        value: "",
        isValid: true,
      },
      dateLastEdited: {
        value: "",
        isValid: true,
      },
      dateCreated: {
        value: "",
        isValid: true,
      },
    },
    false
  );

  // send data to the server!
  const submitHandler = async (event) => {
    event.preventDefault();
    console.log(auth.userId);

    const response = await fetch("http://localhost:5000/api/wounds", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: formState.inputs.name.value,
        bodyPart: formState.inputs.bodyPart.value,
        description: formState.inputs.description.value,
        dateLastEdited: Date.now(),
        dateCreated: Date.now(),
        owner: auth.userId,
      }),
    });

    const responseData = await response.json();
    if (!response.ok) {
      throw new Error(responseData.message);
    }
  };

  return (
    <>
      <form className={classes.root}>
        <Grid container>
          <Grid item xs={6}>
            <TextField variant="outlined" label="Name" value={formState.name.value}></TextField>
          </Grid>
        </Grid>
      </form>
    </>
  );
};

export default NewWound;

