import React from 'react';

import './Input.css';

const Input = props => {
    const changeHandler = event => {};

    const element = props.element === 'input' ? <input onChange = {changeHanler} id={props.id} type={props.type} placeholder={props.plaeholder} /> 
    : <textarea id={props.id} rows={props.rows || 3 } onChange = {changeHanler} />


    return (
        <div className={`form-control`}>
            <label htmlFor={props.id}>{props.label}</label>
            {element}
        </div>
    );
};

export default Input;
