import React, { useContext } from "react";
import Input from "../../shared/components/FormElements/Input";

import {
  VALIDATOR_REQUIRE,
  VALIDATOR_MINLENGTH,
  VALIDATOR_URL,
} from "../../shared/util/validators";
import Button from "../../shared/components/UIElements/Button";
import { useForm } from "../../shared/hooks/form-hook";
import { AuthContext } from "../../shared/context/auth-context";
import { FormControl } from "@material-ui/core";

import "./NewWound.css";

const NewWound = () => {
  const auth = useContext(AuthContext);

  const [formState, changeHandler] = useForm(
    {
      name: {
        value: "",
        isValid: false,
      },
      bodyPart: {
        value: "",
        isValid: false,
      },
      description: {
        value: "",
        isValid: false,
      },
      dateLastEdited: {
        value: "",
        isValid: true,
      },
      dateCreated: {
        value: "",
        isValid: true,
      },
    },
    false
  );

  // send data to the server!
  const submitHandler = async (event) => {
    event.preventDefault();
    console.log(auth.userId);

    const response = await fetch("http://localhost:5000/api/wounds", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: formState.inputs.name.value,
        bodyPart: formState.inputs.bodyPart.value,
        description: formState.inputs.description.value,
        dateLastEdited: Date.now(),
        dateCreated: Date.now(),
        owner: auth.userId,
      }),
    });

    const responseData = await response.json();
    if (!response.ok) {
      throw new Error(responseData.message);
    }
    console.log(responseData);

    // check if json is ok
    console.log(formState.inputs);
  };

  return (
    <>
      <FormControl />
      <form className="wound-form" onSubmit={submitHandler}>
        <p>
          This wound was created on: {formState.dateCreated} and last edited on:{" "}
          {formState.dateLastEdited}.
        </p>
        <Input
          id="name"
          element="input"
          type="text"
          label="Name"
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please enter a valid title!"
          onInput={changeHandler}
        />
        <Input
          id="bodyPart"
          element="input"
          type="text"
          label="Body Part"
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please enter a valid body part!"
          onInput={changeHandler}
        />
        <Input
          id="description"
          element="textarea"
          x
          label="Description"
          validators={[VALIDATOR_MINLENGTH(3)]}
          errorText="Please enter a valid description!"
          onInput={changeHandler}
        />

        <Button type="submit" disabled={!formState.isValid}>
          ADD WOUND
        </Button>
        <p>Is the form valid? {String(formState.isValid)}</p>
      </form>
    </>
  );
};

export default NewWound;
