import React, { useEffect } from 'react';
import { useParams} from 'react-router-dom'

import Input from '../../shared/components/FormElements/Input'
import Button from '../../shared/components/UIElements/Button'
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH} from '../../shared/util/validators';



import './NewSong.css';
import { useForm } from '../../shared/hooks/form-hook';

const DUMMY_SONGS = [
    {
        id: 's1',
        title: 'Jebać PiS',
        description: 'One of the most important songs of 2020 and 2021',
        image: 'https://i.ytimg.com/vi/FQq6Mwv_jpw/maxresdefault.jpg',
        length: '3500',
        creator: 'u1'

    },
    {
        id: 's2',
        title: 'Jebać SąD',
        description: 'One of the most important songs of 2020 and 2021',
        image: 'https://pbs.twimg.com/media/EbXlmjgWsAEy0Ph.png',
        length: '3500',
        creator: 'u2'

    },
]

const UpdatePlace = () => {
    // extract value from route
    const songId = useParams().songId;

    const identifiedSong = DUMMY_SONGS.find(s => s.id === songId);


    // useEffect to only call when state changes!
    useEffect(() => {
        setFormData(
            {
                title: {
                    value: identifiedSong.title,
                    isValid: true
                },
                description: {
                    value: identifiedSong.description,
                    isValid: true
                }
            },
            true
        )
    },[setFormData, identifiedSong]);

    const [formState, changeHandler, setFormData] =useForm(
        {
        title: {
            value: '',
            isValid: false
        },
        description: {
            value: '',
            isValid: false
        }
        },
        false
    );



    const submitHandler = event => {
        event.preventDefault();
        // check if json is ok
        console.log(formState.inputs);
    };




    if (!identifiedSong) {
        return (<div className="center">
            <h2>Could not find place!</h2>
        </div>);
    }
    return <form className="song-form" onSubmit={submitHandler}>
        <Input 
        id="title" 
        element="input"
        type="text"
        label="Title"
        validators={[VALIDATOR_REQUIRE()]}
        onInput={changeHandler}
        errorText="Enter valid title."
        value={formState.inputs.title.value} 
        valid={formState.inputs.title.isValid}
        />
        <Input 
        id="title" 
        element="description"
        type="text"
        label="Description"
        validators={[VALIDATOR_REQUIRE()]}
        errorText="Enter valid description."
        onInput={changeHandler}
        value={formState.inputs.description.value}
        valid={formState.inputs.description.isValid} />
        <Button type="submit" disabled={!formState.isValid}>UPDATE PLACE</Button>
    </form>

};

export default UpdatePlace;
