import React, {useState} from 'react';
import {VALIDATOR_EMAIL, VALIDATOR_REQUIRE, VALIDATOR_MAXLENGTH} from '../../shared/util/validators';
import Input from '../../shared/components/FormElements/Input';
import { useForm } from '../../shared/hooks/form-hook';
import Button from '../../shared/components/UIElements/Button';
import Card from '../../shared/components/UIElements/Button';



const Auth = () => {
    const [isLoginMode, setIsLoginMode] = useState();

    const [formState, changeHandler, setFormData] = 
        useForm(
            {
                email: {
                    value: 'a@b.c',
                    isValid: false
                },
                password: {
                    value: '',
                    isValid: false
                } 
            }, false
    );


    // send data to the server!
    const submitHandler = event => {
        event.preventDefault();
        // check if json is ok
        console.log(formState.inputs);
    };



    const switchModeHandler = () => {
        if (!isLoginMode) {
            setFormData(
                {name: 'undefined'}, 
                formState.inputs.email.isValid && 
                formState.inputs.password.isValid);
        }
        else {
            setFormData({
                ...formState.inputs,
                name: {
                    value: '',
                    isValid: false
                }
            }, false);
        }
        setIsLoginMode(prevMode => !prevMode);
    }

    return (
        <Card>
            <form className="song-form" onSubmit={submitHandler}>
                {!isLoginMode && 
                    <Input 
                        element="input" 
                        id="name" type="text" 
                        label="Your name" 
                        validators={[VALIDATOR_REQUIRE]}
                        errorText="Please enter your name"
                        onInput={changeHandler} />
                }
                <Input id="email"
                    element="input"
                    type="text" 
                    label="Email"
                    validators={[VALIDATOR_REQUIRE(), VALIDATOR_EMAIL()]} 
                    errorText='Please enter a valid email!'
                    onInput={changeHandler}
                    /> 
                <Input id="password"
                    element="input"
                    type="password" 
                    label="Password"
                    validators={[VALIDATOR_REQUIRE(), VALIDATOR_MAXLENGTH(100)]} 
                    errorText='Please enter a valid password!'
                    onInput={changeHandler}
                    /> 
                <Button type="submit" disabled={!formState.isValid}>
                    {isLoginMode ? 'LOGIN' : 'SIGNUP'}
                    </Button>
            </form>
            <Button onClick={switchModeHandler}>Switch</Button> 
        </Card>
    );
}

export default Auth;
