import React from 'react';
import {VALIDATOR_EMAIL, VALIDATOR_REQUIRE, VALIDATOR_MAXLENGTH} from '../../shared/util/validators';
import Input from '../../shared/components/FormElements/Input';
import { useForm } from '../../shared/hooks/form-hook';



const Auth = () => {

    const [formState, changeHandler] = 
        useForm(
            {
                title: {
                    value: '',
                    isValid: false
                },
                description: {
                    value: '',
                    isValid: false
                },
                url: {
                    value: '',
                    isValid: false
                }
            }, false
    );




    // send data to the server!
    const submitHandler = event => {
        event.preventDefault();
        // check if json is ok
        console.log(formState.inputs);
    };

    return (
        <form className="song-form">
            <Input id="email"
                element="input"
                type="text" 
                label="Email"
                validators={[VALIDATOR_REQUIRE(), VALIDATOR_EMAIL()]} 
                errorText='Please enter a valid email!'
                onInput={changeHandler}
                 /> 
            <Input id="password"
                element="input"
                type="password" 
                label="Password"
                validators={[VALIDATOR_REQUIRE(), VALIDATOR_MAXLENGTH()]} 
                errorText='Please enter a valid password!'
                onInput={changeHandler}
                /> 

        </form>
    );
}

export default Auth;
