import React, {useState} from 'react';

export function useForm(initialFValues) {

    const [values, setValues] = useState(initialFValues);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setValues({
          ...values,
          [name]: value,
        });
      };

    return {
        values,
        setValues,
        handleInputChange
    }
}

export function Form() {
    return (
        <>
      <div className={classes.toolbar}></div>
      <div className={classes.toolbar}></div>
      <form className={classes.root}>
        <Grid container>
          <Grid item xs={6}>
            <TextField
              variant="outlined"
              name="name"
              label="Name"
              value={values.name}
              onChange={handleInputChange}
            ></TextField>
            <TextField
              variant="outlined"
              name="bodyPart"
              label="Body Part"
              value={values.bodyPart}
              onChange={handleInputChange}
            ></TextField>
            <TextField
              variant="outlined"
              name="description"
              label="Description"
              value={values.description}
              onChange={handleInputChange}
            ></TextField>
            <TextField
              variant="outlined"
              name="woundImageUpload"
              label="Image URL"
              value={values.woundImageUpload}
              onChange={handleInputChange}
            ></TextField>
          </Grid>
        </Grid>
      </form>
    </>
    )
}
