import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../../shared/context/auth-context";

import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@material-ui/core";
import { DataGrid } from "@material-ui/data-grid";

import { useTheme } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";

import Paper from "@material-ui/core/Paper";
import PageviewIcon from "@material-ui/icons/Pageview";
import Modal from "@material-ui/core/Modal";

import "./WoundTable.css";
import WoundItem from "./WoundItem";

const useStyles = makeStyles((theme) => ({
  table: {
    minWidth: 650,
  },
  paper: {
    position: "absolute",
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    // top: `${top}%`,
    // margin: "auto",
  },
}));

function rand() {
  return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

var rows = [];

const WoundTable = () => {
  //   this.state = {
  //     data: "",
  //   };
  const auth = useContext(AuthContext);
  const userId = useParams().userId;
  const [loadedWounds, setLoadedWounds] = useState([]);
  const [isVisible] = useState(false);

  useEffect(() => {
    const sendRequest = async () => {
      const response = await fetch(
        `http://localhost:5000/api/wounds/user/${userId}`
      );
      const responseData = await response.json();
      if (!response.ok) {
      } else {
        setLoadedWounds(responseData.wound);
        console.log(`Row below`);
      }
    };
    sendRequest();
  }, []);

  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  const handleClick = (id, column) => {
    return (event) => {
      console.log(`You clicked on row with id ${id}, in column ${column}.`);
    };
  };



  const classes = useStyles();

  var self = this;



  function handleCellClick(rowNumber, columnNumber, evt) {
    console.log("activityId", evt.target.dataset.myRowIdentifier);
  }

  const columns = [
    { field: "id", headerName: "ID", width: 200 },
    { field: "name", headerName: "First name", width: 130 },
    { field: "bodyPart", headerName: "Body part", width: 130 },
    {
      field: "description",
      headerName: "Description",
      description: "This column has a value getter and is not sortable.",
      sortable: false,
      width: 160,
    },
    { field: "dateCreated" },
  ];

  //   const openModal = (props) => {
  //       render() {
  //           return (
  //               <WoundItem />
  //           );
  //       };
  //   }

  return (
    <>
      <TableContainer component={Paper} className="table">
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell align="right">Body Part</TableCell>
              <TableCell align="right">Description</TableCell>
              <TableCell align="right">Last Edited</TableCell>
              <TableCell align="right">Created On</TableCell>
              <TableCell align="right"></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {loadedWounds.map((loadedWound) => (
              <TableRow key={loadedWound.id}>
                <TableCell component="th" scope="row">
                  {loadedWound.name}
                </TableCell>
                <TableCell align="right">{loadedWound.bodyPart}</TableCell>
                <TableCell align="right">{loadedWound.description}</TableCell>
                <TableCell align="right">
                  {loadedWound.dateLastEdited}
                </TableCell>
                <TableCell align="right">{loadedWound.dateCreated}</TableCell>
                <TableCell align="right">
                  <Button
                    type="button"
                    onClick={() => (
                      <>
                        <Modal
                          open={open}
                          onClose={handleClose}
                          aria-labelledby="simple-modal-title"
                          aria-describedby="simple-modal-description"
                        >
                          <p>{loadedWound.id}</p>
                        </Modal>
                      </>
                    )}
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <div className="table" style={{ height: 400, width: "100%" }}>
        <DataGrid
          rows={loadedWounds}
          columns={columns}
          pageSize={5}
          //   checkboxSelection
        />
      </div>
    </>
  );
};

export default WoundTable;
