import React, { useState, useCallback } from "react";
import {
  BrowserRouter as Router,
  Route,
  Redirect,
  Switch,
} from "react-router-dom";

import Users from "./users/pages/Users";
import SideNavigation from "./shared/components/navigation/SideNavigation"
import NewSong from "./songs/pages/NewSong";
import MainNavigation from "./shared/components/navigation/MainNavigation";
import UserSongs from "./songs/pages/UserSongs";
import UpdatePlace from "./songs/pages/UpdateSong";
import Auth from "./users/pages/Auth";
import { AuthContext } from "./shared/context/auth-context.js";
import { ProSidebar, Menu, MenuItem, SubMenu } from "react-pro-sidebar";
import "react-pro-sidebar/dist/css/styles.css";

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userId, setUserId] = useState(false);

  const login = useCallback((uid) => {
    setIsLoggedIn(true);
    setUserId(uid);
  }, []);

  const logout = useCallback(() => {
    setIsLoggedIn(false);
    setUserId(null);
  }, []);

  let routes;

  if (isLoggedIn) {
    routes = (
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/songs" exact>
          <UserSongs />
        </Route>
        <Route path="/songs/new" exact>
          <NewSong />
        </Route>
        <Route path="/songs/:songId">
          <UpdatePlace />
        </Route>
        <Route path="/auth">
          <Auth />
        </Route>
        <Redirect to="/" />
      </Switch>
    );
  } else {
    routes = (
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/songs" exact>
          <UserSongs />
        </Route>
        <Route path="/auth">
          <Auth />
        </Route>
        <Redirect to="/auth" />
      </Switch>
    );
  }

  return (
    <AuthContext.Provider
      value={{
        isLoggedIn: isLoggedIn,
        userId: userId,
        login: login,
        logout: logout,
      }}
    >
      <Router>

        <MainNavigation />
        <main>{routes}</main>

        <SideNavigation />
      </Router>
    </AuthContext.Provider>
  );
};
export default App;
